generated.css:
html,
body {
  height: 100%;
}

body {
  font: 400 10px 'arial';
  background: #fafafa;
}

* {
  margin: 0;
  padding: 0;
}

input {
  max-width: 5ch;
}

header {
  color: #333;
  padding: 4px 0;
  text-align: center;
  border-bottom: 1px solid #ccc;
  display: flex;
  align-items: center;
  justify-content: center;
}

header > * {
  margin: 0 4px;
}
.frameless {
  background: none;
  border: none;
  outline: none;
}

.wrapper {
  margin-top: 10px;
  height: calc( 100vh - 47px);
  position: relative;
  pointer-events: none;
}

.scaled {
  overflow: hidden
}

.viewport {
  transition: all 0.4s ease-in-out 0s;
  transform-origin: center center;
  position: absolute;
  left: 50%;
  top: 50%;
  backface-visibility: hidden;
  transform: translate(-50%, -50%);
}

#deviceColors button {
  width: 20px;
  height: 20px;
  border-radius: 10px;
  outline: none;
  border: none;
  margin: 0 3px;
  box-shadow: 0 0 1px 0px #000;
}

iframe {
  width: 100%;
  background-color: white;
  margin: auto;
  border: none;
}

.marvel-device.no-device {
  padding: 0;
  box-sizing: border-box !important;
  border: none !important;
  background: #fafafa !important;
  box-shadow: none  !important;
  border-radius: 0;
  overflow: hidden;
}
.no-device .screen {
  z-index: 9;
}

.marvel-device .screen {
  overflow: auto;
  pointer-events: auto;
}
